import apiClient from '../../config/axios';
import { TimeLog, CreateTimeLogRequest, UpdateTimeLogRequest, ApiResponse, PaginatedResponse } from '../../types';

// Backend API response format for task logs
interface TaskLogResponse {
  id: string;
  userId: {
    id: string;
    name: string;
    email: string;
    role: string;
  };
  date: string;
  totalHours: number;
  tasks: Array<{
    project_name: string;
    description: string;
    hours: number;
  }>;
  createdAt: string;
  updatedAt: string;
}

interface TaskLogCreateRequest {
  userId: string;
  date: string;
  totalHours: number;
  tasks: Array<{
    project_name: string;
    description: string;
    hours: number;
  }>;
}

class TimesheetService {
  /**
   * Get all task logs
   */
  async getTaskLogs(userId?: string, startDate?: string, endDate?: string, projectName?: string): Promise<ApiResponse<{ taskLogs: TaskLogResponse[] }>> {
    try {
      const params = new URLSearchParams();
      if (userId) params.append('userId', userId);
      if (startDate) params.append('startDate', startDate);
      if (endDate) params.append('endDate', endDate);
      if (projectName) params.append('project_name', projectName);

      const response = await apiClient.get<ApiResponse<{ taskLogs: TaskLogResponse[] }>>(`/tasklogs?${params.toString()}`);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to fetch task logs');
    }
  }

  /**
   * Get single task log by user ID and date
   */
  async getTaskLogByUserAndDate(userId: string, date: string): Promise<ApiResponse<{ taskLog: TaskLogResponse }>> {
    try {
      const params = new URLSearchParams({ userId, date });
      const response = await apiClient.get<ApiResponse<{ taskLog: TaskLogResponse }>>(`/tasklogs/single?${params.toString()}`);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to fetch task log');
    }
  }

  /**
   * Create new time log
   */
  async createTimeLog(timeLogData: CreateTimeLogRequest): Promise<ApiResponse<TimeLog>> {
    try {
      const response = await apiClient.post<ApiResponse<TimeLog>>('/timesheet', timeLogData);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to create time log');
    }
  }

  /**
   * Update time log
   */
  async updateTimeLog(id: string, timeLogData: UpdateTimeLogRequest): Promise<ApiResponse<TimeLog>> {
    try {
      const response = await apiClient.put<ApiResponse<TimeLog>>(`/timesheet/${id}`, timeLogData);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to update time log');
    }
  }

  /**
   * Delete time log
   */
  async deleteTimeLog(id: string): Promise<ApiResponse<void>> {
    try {
      const response = await apiClient.delete<ApiResponse<void>>(`/timesheet/${id}`);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to delete time log');
    }
  }

  /**
   * Get time logs by employee ID
   */
  async getTimeLogsByEmployee(employeeId: string, page = 1, limit = 10): Promise<PaginatedResponse<TimeLog>> {
    try {
      const response = await apiClient.get<PaginatedResponse<TimeLog>>(`/timesheet/employee/${employeeId}?page=${page}&limit=${limit}`);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to fetch employee time logs');
    }
  }

  /**
   * Get time logs by project ID
   */
  async getTimeLogsByProject(projectId: string, page = 1, limit = 10): Promise<PaginatedResponse<TimeLog>> {
    try {
      const response = await apiClient.get<PaginatedResponse<TimeLog>>(`/timesheet/project/${projectId}?page=${page}&limit=${limit}`);
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to fetch project time logs');
    }
  }

  /**
   * Create multiple time log entries in a batch operation
   */
  async createMultipleTimeLogs(timeLogEntries: CreateTimeLogRequest[]): Promise<ApiResponse<TimeLog[]>> {
    try {
      const response = await apiClient.post<ApiResponse<TimeLog[]>>('/timesheet/batch', { entries: timeLogEntries });
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to create time log entries');
    }
  }

  /**
   * Get time logs by employee with date range filtering
   */
  async getTimeLogsByEmployeeAndDateRange(
    employeeId: string, 
    startDate?: string, 
    endDate?: string, 
    page = 1, 
    limit = 100
  ): Promise<PaginatedResponse<TimeLog>> {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });

      if (startDate) params.append('startDate', startDate);
      if (endDate) params.append('endDate', endDate);

      const response = await apiClient.get<PaginatedResponse<TimeLog>>(
        `/timesheet/employee/${employeeId}?${params.toString()}`
      );
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.message || 'Failed to fetch employee time logs');
    }
  }
}

export const timesheetService = new TimesheetService();
