import React from 'react';
import { cn } from '../../utils/helpers';
import TopNavbar from './TopNavbar';

export interface AppLayoutProps {
  children: React.ReactNode;
  title?: string;
  subtitle?: string;
  className?: string;
}

const AppLayout: React.FC<AppLayoutProps> = ({
  children,
  title,
  subtitle,
  className,
}) => {
  return (
    <div className="min-h-screen bg-excel-background flex flex-col">
      {/* Top Navigation Bar */}
      <TopNavbar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Page Header (optional) */}
        {(title || subtitle) && (
          <div className="bg-white border-b border-excel-border px-excel-xl py-excel-lg">
            <div>
              {title && (
                <h1 className="text-excel-2xl font-semibold text-excel-text-primary">
                  {title}
                </h1>
              )}
              {subtitle && (
                <p className="text-excel-base text-excel-text-muted mt-excel-xs">
                  {subtitle}
                </p>
              )}
            </div>
          </div>
        )}

        {/* Page Content */}
        <main className={cn('flex-1 p-excel-xl', className)}>
          {children}
        </main>
      </div>
    </div>
  );
};

export default AppLayout;
