import React, { useState, useEffect } from 'react';
import DashboardLayout from '../components/layouts/DashboardLayout';
import Button from '../components/ui/Button';
import Select from '../components/ui/Select';
import { cn } from '../utils/helpers';
import { Download, Calendar, Loader2, AlertCircle, RefreshCw } from 'lucide-react';
import { SAMPLE_LOG_DATA } from '../constants/sampleLogData';
import { DUMMY_PROJECTS } from '../constants/dummyData';
import { DUMMY_USERS } from '../constants/dummyData';
import { reportsService, ProjectReport, GrandReportData } from '../services/reports/reports.service';

interface ReportProps {}

interface ReportData {
  project: string;
  totalHours: number;
  PM: number;
  QA: number;
  DEV: number;
  DESIGN: number;
}

const Report: React.FC<ReportProps> = () => {
  const [selectedMonth, setSelectedMonth] = useState(new Date().toISOString().slice(0, 7)); // YYYY-MM format

  // API state management
  const [apiReportData, setApiReportData] = useState<GrandReportData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [useRealData, setUseRealData] = useState(true); // Default to API data

  // Fetch report data from API
  const fetchReportData = async (month?: string) => {
    setIsLoading(true);
    setError(null);

    try {
      let startDate: string | undefined;
      let endDate: string | undefined;

      if (month) {
        // Convert YYYY-MM to date range
        const [year, monthNum] = month.split('-');
        const firstDay = new Date(parseInt(year), parseInt(monthNum) - 1, 1);
        const lastDay = new Date(parseInt(year), parseInt(monthNum), 0);

        startDate = firstDay.toISOString().split('T')[0];
        endDate = lastDay.toISOString().split('T')[0];
      }

      const response = await reportsService.getGrandReport(startDate, endDate);
      setApiReportData(response.data);
      console.log('Report data fetched:', response.data);
    } catch (err) {
      console.error('Failed to fetch report data:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch report data');
      setApiReportData(null);
    } finally {
      setIsLoading(false);
    }
  };

  // Generate month options for the last 12 months
  const generateMonthOptions = () => {
    const options = [];
    const currentDate = new Date();

    for (let i = 0; i < 12; i++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
      const value = date.toISOString().slice(0, 7);
      const label = date.toLocaleDateString('en-US', { year: 'numeric', month: 'long' });
      options.push({ value, label });
    }

    return options;
  };

  // Fetch data on component mount
  useEffect(() => {
    if (useRealData) {
      fetchReportData(selectedMonth);
    }
  }, []);

  // Fetch data when month changes (only if using real data)
  useEffect(() => {
    if (useRealData) {
      fetchReportData(selectedMonth);
    }
  }, [selectedMonth, useRealData]);

  // Process API data to match the existing ReportData format
  const processApiReportData = (): ReportData[] => {
    if (!apiReportData) return [];

    return apiReportData.projects.map(project => ({
      project: project.project,
      totalHours: project.totalHours,
      PM: project.PM,
      QA: project.QA,
      DEV: project.DEV,
      DESIGN: project.DESIGN,
    }));
  };

  // Process log data to generate report (dummy data)
  const generateReportData = (): ReportData[] => {
    const reportData: { [key: string]: ReportData } = {};

    // Initialize all projects
    DUMMY_PROJECTS.forEach(project => {
      reportData[project.name] = {
        project: project.name,
        totalHours: 0,
        PM: 0,
        QA: 0,
        DEV: 0,
        DESIGN: 0,
      };
    });

    // Process log entries for selected month
    const [year, month] = selectedMonth.split('-');
    SAMPLE_LOG_DATA.forEach(log => {
      const logDate = new Date(log.date);
      if (logDate.getFullYear() === parseInt(year) && logDate.getMonth() === parseInt(month) - 1) {
        log.tasks.forEach(task => {
          if (reportData[task.projectName]) {
            reportData[task.projectName].totalHours += task.hours;

            // Find user role for this task (simplified - using first user as example)
            const userRole = DUMMY_USERS[0].role; // In real app, this would be based on actual task assignment
            switch (userRole) {
              case 'PM':
                reportData[task.projectName].PM += task.hours;
                break;
              case 'QA':
                reportData[task.projectName].QA += task.hours;
                break;
              case 'DEV':
                reportData[task.projectName].DEV += task.hours;
                break;
              case 'DESIGN':
                reportData[task.projectName].DESIGN += task.hours;
                break;
            }
          }
        });
      }
    });

    return Object.values(reportData);
  };

  const reportData = generateReportData();

  // Calculate summary totals
  const summaryTotals = reportData.reduce(
    (totals, row) => ({
